import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';

import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    width: '25ch',
    fontSize: 30,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  input: () => ({
    fontFamily: `${theme.fonts.tabs.family} !important`,
    fontWeight: theme.fonts.tabs.weight,
    fontSize: 18,
    color: theme.colors.secondary,
    height: 24
  }),
}));


export default function SimpleSelect() {
  const classes = useStyles();
  const [age, setAge] = React.useState('');

  const handleChange = (event) => {
    setAge(event.target.value);
  };

  return (
    <div>
      <FormControl variant="outlined" className={classes.formControl}>
        <InputLabel id="demo-simple-select-outlined-label">Reden afschrijven</InputLabel>
        <Select
          labelId="demo-simple-select-outlined-label"
          id="demo-simple-select-outlined"
          value={age}
          onChange={handleChange}
          label="Reden afschrijven"
          className="AXIDropdown"
        >
          <MenuItem value={10}>Beschadiging</MenuItem>
          <MenuItem value={20}>Over datum</MenuItem>
          <MenuItem value={30}>Incompleet</MenuItem>
        </Select>
      </FormControl>
    </div>
  );
}
