import React from 'react';
import { db } from '../services/Firebase.js';

import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import IconButton from '@material-ui/core/IconButton';
import FitnessCenterIcon from '@material-ui/icons/FitnessCenter';
import EditIcon from '@material-ui/icons/Edit';


class ChestEx extends React.Component {

    state = { 
        users: null
    };

    componentDidMount(){
        console.log('mounted');
        db.collection('users').doc('zpXCkFVQHE6TiAhKmlvu').collection('bankdrukken').orderBy('date', 'asc').limit(1)
            .get()
            .then( snapshot => {
                const users = []
                snapshot.forEach(doc => {
                    const data = doc.data();
                    users.push(data);
                });
                //console.log(snapshot);
                this.setState({ users: users});
        })
        .catch( error => console.log(error));
    };

    render(){
        let iconStyles = {
            fontSize: '18px',
          };

        return (
                <List className="list">


                {
                    this.state.users && this.state.users.map( user => {
                        const text = user.reps1 + "x " + user.weight1 + "kg - " + user.reps2 + "x " + user.weight2 + "kg - " + user.reps3 + "x " + user.weight3 + "kg";
                        return (
                            <div>
                            <ListItem>
                                <ListItemAvatar>
                                    <Avatar>
                                    <FitnessCenterIcon />
                                    </Avatar>
                                </ListItemAvatar>
                                <ListItemText primary="Bankdrukken" secondary={text} />
                                <ListItemSecondaryAction>
                                    <IconButton edge="end" aria-label="edit">
                                    <EditIcon style={iconStyles}/>
                                    </IconButton>
                                </ListItemSecondaryAction>
                            </ListItem>
                            </div>
                        )
                    })
                }
                </List>
        )
    };
}
export default ChestEx;
